import React from "react";
import { connect } from "react-redux";
import { Combobox } from "react-widgets";
import de from "../../images/de.png";
import fr from "../../images/fr.png";
import it from "../../images/it.png";
import en from "../../images/en.png";
import sq from "../../images/sq.png";
import { updateLanguage } from "../../actions/utilityActions";

export class LanguageToggle extends React.Component {

    handleLanguageChange = (value) => {
        this.setState({
            language: value
        })
    }

    changeLanguage = (value) => {
        console.log(this.props);
        this.props.updateLanguage(value);
    }


    render() {
        let ListItem = ({ item }) => (
            <React.Fragment>
                <span>
                    {item === "de" ? (
                        <img src={de} width="20px" height="20px" alt={item}></img>
                    ) : (
                            ""
                        )}
                    {item === "sq" ? (
                        <img src={sq} width="20px" height="20px" alt={item}></img>
                    ) : (
                            ""
                        )}
                    {item === "fr" ? (
                        <img src={fr} width="20px" height="20px" alt={item}></img>
                    ) : (
                            ""
                        )}
                    {item === "it" ? (
                        <img src={it} width="20px" height="20px" alt={item}></img>
                    ) : (
                            ""
                        )}
                    {item === "en" ? (
                        <img src={en} width="20px" height="20px" alt={item}></img>
                    ) : (
                            ""
                        )}{" "}
                    <span style={{ textTransform: 'uppercase' }} >{item}</span>
                </span>
            </React.Fragment>
        );

        let languages = ["de", "en", "it", "fr", "sq"];

        let lang = this.props.language;

        return (
            <Combobox onChange={this.changeLanguage} defaultValue={lang} containerClassName="languagebox" data={languages} itemComponent={ListItem} />
        );
    }
}


const mapstateToProps = state => {
    return {
        language: state.utility.language
    };
};

export default connect(
    mapstateToProps,
    { updateLanguage }
)(LanguageToggle);
