import React, { Component } from "react";
import { connect } from "react-redux";
import { Modal, Button } from "react-bootstrap";
import { Carousel } from "react-responsive-carousel";
import { Combobox } from "react-widgets";
import de from "../../../../../images/de.png";
import fr from "../../../../../images/fr.png";
import it from "../../../../../images/it.png";
import en from "../../../../../images/en.png";
import "react-responsive-carousel/lib/styles/carousel.min.css";
import { submitModalTitle, submitModalConvert } from '../../../../../translations/translations';

class SubmitRDFaModal extends Component {
    state = {
        language: 'en'
    };

    componentWillMount() { }

    handleSave = e => {
        e.preventDefault();
    };

    handleConverting = () => {
        this.props.generateHTML(this.state.language);
    };

    handleLanguageChange = (value) => {
        this.setState({
            language: value
        })
    }

    render() {
        let ListItem = ({ item }) => (
            <React.Fragment>
                <span>
                    {item === "de" ? (
                        <img src={de} width="20px" height="20px" alt={item}></img>
                    ) : (
                            ""
                        )}
                    {item === "fr" ? (
                        <img src={fr} width="20px" height="20px" alt={item}></img>
                    ) : (
                            ""
                        )}
                    {item === "it" ? (
                        <img src={it} width="20px" height="20px" alt={item}></img>
                    ) : (
                            ""
                        )}
                    {item === "en" ? (
                        <img src={en} width="20px" height="20px" alt={item}></img>
                    ) : (
                            ""
                        )}{" "}
                    <span style={{ textTransform: "uppercase" }}>{item}</span>
                </span>
            </React.Fragment>
        );

        let languages = ["de", "en", "it", "fr"];

        let lang = this.props.language;

        return (
            <Modal
                show={this.props.show}
                onHide={this.props.onHide}
                size="lg"
                aria-labelledby="contained-modal-title-vcenter"
                dialogClassName="submit-modal"
                centered
            >
                <Modal.Header closeButton>
                    <Modal.Title id="contained-modal-title-vcenter">
                        {submitModalTitle[lang]}
                    </Modal.Title>
                </Modal.Header>
                <Modal.Body>
                    <Combobox
                        onChange={this.handleLanguageChange}
                        defaultValue={"en"}
                        containerClassName="languagebox submit-window"
                        data={languages}
                        itemComponent={ListItem}
                    />{" "}
                </Modal.Body>
                <Modal.Footer style={{ justifyContent: "center" }}>
                    <Button variant="primary" onClick={this.handleConverting}>
                        {submitModalConvert[lang]}
                    </Button>
                </Modal.Footer>
            </Modal>
        );
    }
}

const mapstateToProps = state => {
    return {
        language: state.utility.language
    };
};

export default connect(
    mapstateToProps,
    {}
)(SubmitRDFaModal);
